(6, '٦', 'لايحبّ الله'),
(7, '٧', 'واذاسمعوا'),
(8, '٨', 'ولوانّنا'),
(9, '٩', 'قال الملأ'),
(10, '١٠', 'واعلموا'),
(11, '١١', 'يعتذرون'),
(12, '١٢', 'ومامن دآبّة'),
(13, '١٣', 'ومآابرّئ'),
(14, '١٤', 'ربما'),
(15, '١٥', 'سبحٰن الّذي'),
(16, '١٦', 'قال الم'),
(17, '١٧', 'اقترب'),
