(19, '١٩', 'وقل الّذين'),
(20, '٢٠', 'امّن خلق'),
(21, '٢١', 'اتل مآاوحي'),
(22, '٢٢', 'ومن يّقنت'),
(23, '٢٣', 'ومالي'),
(24, '٢٤', 'فمن اظلم'),
(25, '٢٥', 'اليه يردّ'),
(26, '٢٦', 'حٰمۤ'),
