INSERT INTO  surah  ( id_surah ,  no_arab ,  nama_surah ,  arab ,  arti ,  jml_ayat ,  tipe ) VALUES
(1, '١', 'Al-Fatihah', 'الفاتحة', 'Pembukaan', 7, 'Makkiyah'),
(2, '٢', 'Al-Baqarah', 'البقرة', 'Sapi', 286, 'Madaniyah'),
(3, '٣', 'Ali  ''Imran', 'اٰل عمران', 'Keluarga Imran', 200, 'Madaniyah'),
(4, '٤', 'An-Nisa ''', 'النساۤء', 'Wanita', 176, 'Madaniyah'),
(5, '٥', 'Al-Ma ''idah', 'الماۤئدة', 'Hidangan', 120, 'Madaniyah'),
(6, '٦', 'Al-An ''am', 'الانعام', 'Binatang Ternak', 165, 'Makkiyah'),
(7, '٧', 'Al-A ''raf', 'الاعراف', 'Tempat Tertinggi', 206, 'Makkiyah'),
